            for(i=0;i<4;++i) s+=y[i]*k[i];
            if (s>=x) {
                int m=0;
                int p=s-x;
                int c2[4]={0};
                int ok=1;
                for(i=3;i>=0;--i) {
                    c2[i]=p/y[i];
                    p%=y[i];
                    if (c2[i] && k[i]) ok=0;
                    m+=c[i]-k[i]+c2[i];
                }
                if (n>m && ok) {
                    for(i=0;i<4;++i) res[i]=k[i];
                    n=m;
                }
            }
        }
        for(i=0;i<4;++i) if (res[i]) printf("%d %d\n", y[i], res[i]);
        puts("");
    }
    return 0;
}

