		purse[0]-=(bill%50)/10;
		pay[0]+=(bill%50)/10;
		bill-=(bill%50);

		pay[0]+=(purse[0]/5)*5;
		bill-=(purse[0]/5)*50;
	}else{
		bill+=50-(bill%50);
	}

