		bill-=(bill%100);

		pay[1]+=(purse[1]/2)*2;
		bill-=(purse[1]/2)*100;
	}else{
		bill+=100-(bill%100);
	}

	if(purse[2]>=(bill%500)/100){
		purse[2]-=(bill%500)/100;
		pay[2]+=(bill%500)/100;
		bill-=(bill%500);

		pay[2]+=(purse[2]/5)*5;
