}state;

state first_state;

state dfs(state s, int payment, int cnt)
{
  state st[4];
  state u = {0}, min = {0, 0, 0, 0, INT_MAX};
  int i, x = 0;

  for(i = 0; i < 4; i++)
    {
      /* st[i].ten = 0; */
      /* st[i].fifty = 0; */
      /* st[i].hundred = 0; */
      /* st[i].five_hundreds = 0; */
      /* st[i].total_coins_in_purse = INT_MAX; */
