		int count = 0;
		for(int i=0;i<4;i++) {
			count += b/k[i];
			backk[i] = b/k[i];
			b = b%k[i];
		}
		return count;
	}

	static boolean check(int i,int j,int k,int l) {
		if( (i!=0 && backk[0]!=0) || (j!=0 && backk[1]!=0) || (k!=0 && backk[2]!=0) || (l!=0 && backk[3]!=0) ){
			return false; 
		}else {
			return true;
		}
	}

