			retHund5++;
		}
		while(returnMon >= 100){
			returnMon = returnMon - 100;
			retHund++;
		}
		while(returnMon >= 50){
			returnMon = returnMon - 50;
			retTen5++;
		}
		while(returnMon >= 10){
