		boolean first = true;

		while (true) {
			final int price = sc.nextInt();

			if (price == 0) {
				break;
			}

			if(first){
				first = !first;
			}else{
				System.out.println();
			}

			int[] coins = new int[4];

			for (int i = 0; i < 4; i++) {
				coins[i] = sc.nextInt();
			}

			int min = Integer.MAX_VALUE;
			int m_i = 0;
			int m_j = 0;
			int m_k = 0;
			int m_l = 0;

			for (int i = 0; i <= coins[0]; i++) {
				for (int j = 0; j <= coins[1]; j++) {
					for (int k = 0; k <= coins[2]; k++) {
						for (int l = 0; l <= coins[3]; l++) {
							final int sum = i * 10 + j * 50 + k * 100 + l * 500;

							if (price > sum) {
								continue;
							}

							final int diff = sum - price;
							int n_l = diff / 500;
							int n_k = (diff - n_l * 500) / 100;
