	public static int calc(int n, int ten, int fifty, int hund, int fihund){
		int rest = (ten * 10 + fifty * 50 + hund * 100 + fihund * 500) - n;

		int r_ten = 0;
		int r_fifty = 0;
		int r_hund = 0;
		int r_fihund = 0;

		r_fihund = (rest / 500);
		rest -= r_fihund * 500;
		r_hund = (rest / 100);
		rest -= r_hund * 100;
		r_fifty = (rest / 50);
		rest -= r_fifty * 50;
		r_ten = (rest / 10);
		rest -= r_ten * 10;

		return r_ten + r_fifty + r_hund + r_fihund;
	}

