	final int MOD = 1000000007;
	int[] dx = { 1, 0, 0, -1 };
	int[] dy = { 0, 1, -1, 0 };

	int[] coin;
	int[] use;
	int[] ans;
	int[] m = { 1, 10, 50, 100 };
	int money;
	int min;

	void run() {
		boolean f = false;
		for (;;) {
			money = sc.nextInt();
			if (money == 0) {
				return;
			}
			if (f) {
				System.out.println();
			}
			f = true;
			coin = sc.nextIntArray(4);
			use = new int[4];
			ans = new int[4];
			min = Integer.MAX_VALUE;
			dfs(0, 0);
			for (int i = 0; i < 4; i++) {
				if (ans[i] != 0) {
					System.out.println(m[i] + " " + ans[i]);
				}
			}
		}
	}

	void dfs(int index, int sum) {
		if (index == 4) {
			int res = use[0] * 10 + use[1] * 50 + use[2] * 100 + use[3] * 500;
			if (res >= money) {
				int sub = res - money;
				int cnt = 0;
				for (int i = 3; i >= 0; i--) {
					int cc = sub / m[i];
					if (cc != 0) {
						sub -= m[i] * cc;
						cnt += cc;
					}
				}
				if (min > cnt) {
					min = cnt;
					for (int i = 0; i < 4; i++) {
						ans[i] = use[i];
					}
				}
			}
			return;
		}
		for (int i = 0; i <= coin[index]; i++) {
			use[index] = i;
			dfs(index + 1, sum + m[index] * i);
		}
	}

