}

import java.util.Scanner;

public class Main {

    static int[] cache;
    static int[] ret;
    static int[] cacheType = {10, 50, 100, 500};

    static int[] change(int c) {
        int rest[] = new int[4];
        int amount = c;
        for (int i = 3; i >= 0; i--) {
            rest[i] = amount / cacheType[i];
            amount -= rest[i] * cacheType[i];
        }

        return rest;
    }

    static void printRet(int[] rest) {
        for(int i = 0; i < 4; i++) {
            if (rest[i] != 0) {
                System.out.println(cacheType[i] + " " + rest[i]);
            }
        }
    }

