                break;
            int[] ans = new int[4];

            cache = new int[4];
            for (int i = 0; i < 4; i++) {
                cache[i] = Integer.parseInt(sc.next());
                sum += cache[i] * cacheType[i];
            }
            int c = sum - pay;
