	int calc(int x) {
		if (x < 0) return Integer.MAX_VALUE;

		int res = 0;
		for (int i = 3; i >= 0; i--) {
			while (x >= VAL[i]) {
				x -= VAL[i];
				res++;
			}
		}
		return res;
	}

