import java.util.Scanner;

public class Main {
    static final int[] price = {10, 50, 100, 500};
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int count = 0; // 

        while (true) {
            int pay = sc.nextInt();
            if (pay == 0) {
                break;
            }

            count++;
            int[] coin = new int[4]; // coin[0-3]: 10, 50, 100, 500
            int[] result = new int[4];
            for (int i = 0; i < coin.length; i++) {
                coin[i] = sc.nextInt();
            }

            int total = coin[0] * 10 + coin[1] * 50 + coin[2] * 100 + coin[3] * 500;
            change(total - pay, result);
            for (int i = 0; i < coin.length; i++) {
                int n = coin[i] - result[i];
                if (n > 0) {
                    System.out.println(price[i] + " " + n);
                }
            }
            if (count > 1) {
                System.out.println();
            }
        }
    }

    public static void change(int total, int[] result) {
        for (int i = price.length - 1; i >= 0; i--) {
            int n = total / price[i];
            result[i] += n;
            total -= n * price[i];
        }
    }
}

