	void dfs(int index, int sum) {
		if (index == 4) {
			int res = use[0] * 10 + use[1] * 50 + use[2] * 100 + use[3] * 500;
			if (res >= money) {
				int sub = res - money;
				int cnt = 0;
				for (int i = 3; i >= 0; i--) {
					int cc = sub / m[i];
					if (cc != 0) {
						sub -= m[i] * cc;
						cnt += cc;
