using System.Linq;


    class Program
    {
        static List<int> Coins = new List<int>() { 10, 50, 100, 500 };

        private static void Main(string[] args)
        {
            while (true)
            {
                // 
                var bill = int.Parse(Console.ReadLine());
                if (bill == 0) break;

                // 
                var ownedCoins = Console.ReadLine().Trim().Split(' ').Select(i => int.Parse(i)).ToList();
                // 
                var ownedMoney = Coins.Zip(ownedCoins, (c1, c2) => c1 * c2).Sum();

                // 
                var changeMoney = ownedMoney - bill;
                // 
                var changeCoins = Coins.Reverse<int>().Select(c => GetChange(ref changeMoney, c)).Reverse().ToList();

                // 
                var paymentCoins = ownedCoins.Zip(changeCoins, (c1, c2) => c1 - c2).ToList();

                WriteLines(paymentCoins);
            }
        }

        private static int GetChange(ref int money, int coin)
        {
            var result = money / coin;
            money = money % coin;
            return result;
        }

        private static void WriteLines(List<int> paymentCoins)
        {
            for (int i = 0; i < Coins.Count(); i++)
            {
                if (paymentCoins[i] > 0)
                {
                    Console.WriteLine("{0} {1}", Coins[i], paymentCoins[i]);
                }
            }
            Console.WriteLine();
        }
    }

