namespace Make_Purse_Light
{
    class Program
    {
        static List<int> Coins = new List<int>() { 10, 50, 100, 500 };
        static bool flag = false;

        private static void Main(string[] args)
        {
            while (true)
            {
                // 
                int bill = int.Parse(Console.ReadLine());
                if (bill == 0) break;

                // 
                List<int> ownedCoins = Console.ReadLine().Trim().Split(' ').Select(i => int.Parse(i)).ToList();
                // 
                int ownedMoney = GetMoney(Coins, ownedCoins);

                // 
                int changeMoney = ownedMoney - bill;
                // 
                List<int> changeCoins = Coins.Reverse<int>().Select(c => GetChange(ref changeMoney, c)).Reverse().ToList();

                // 
                List<int> paymentCoins = GetPayment(ownedCoins, changeCoins);

                WriteLines(paymentCoins);
            }
        }

        private static int GetChange(ref int money, int coin)
        {
            if (flag) Console.WriteLine(); else flag = true;
            var result = money / coin;
            money = money % coin;
            return result;
        }

        private static void WriteLines(List<int> paymentCoins)
        {
            for (int i = 0; i < Coins.Count(); i++)
            {
                if (paymentCoins[i] > 0)
                {
                    Console.WriteLine("{0} {1}", Coins[i], paymentCoins[i]);
                }
            }
        }

        private static int GetMoney(List<int> l1, List<int> l2)
        {
            int result = 0;
            for (int i = 0; i < l1.Count(); i++)
            {
                result += l1[i] * l2[i];
            }
            return result;
        }


        private static List<int> GetPayment(List<int> l1, List<int> l2)
        {
            List<int> result = new List<int>();
            for (int i = 0; i < l1.Count(); i++)
            {
                result.Add(l1[i] - l2[i]);
            }
            return result;
        }
    }
