class charge:
    def __init__(self,x,y,z,v):
        self.coins = {10:x,50:y,100:z,500:v}
    def get_sum(self):
        return self.coins[10]*10 + self.coins[50]*50+self.coins[100]*100 + self.coins[500]*500
    def how_sheet(self):
        return self.coins[10] + self.coins[50] +self.coins[100] + self.coins[500]


def cal_charge(price,payment):
    a =[0,0,0,0]
    temp = payment - price
    if temp //500 !=0:
        a[3] = temp // 500
        temp = temp % 500
    if temp //100 !=0:
        a[2] = temp // 100
        temp = temp % 100
    if price //50 !=0:
        a[1] = temp // 50
        temp = temp % 50
    a[0] = temp //10
    return charge(*a)

