use std::cmp::min;
use std::io::{Read, StdinLock};
use std::str::{from_utf8, FromStr};
use std::vec::Vec;

struct StdinReader<'a> {
    reader: StdinLock<'a>,
}

impl<'a> StdinReader<'a> {
    pub fn new(reader: StdinLock<'a>) -> StdinReader {
        StdinReader { reader: reader }
    }

    pub fn read<T: FromStr>(&mut self) -> T {
        fn is_whitespace(ch: u8) -> bool {
            ch == 0x20 || ch == 0x0a || ch == 0x0d
        }

        let token: Vec<u8> = self.reader
            .by_ref()
            .bytes()
            .map(|ch| ch.expect("failed to read a byte"))
            .skip_while(|ch| is_whitespace(*ch))
            .take_while(|ch| !is_whitespace(*ch))
            .collect();
        let token_str = from_utf8(&token)
            .unwrap_or_else(|_| panic!(format!("invalid utf8 sequence: {:?}", token)));
        token_str
            .parse()
            .unwrap_or_else(|_| panic!(format!("failed to parse input: {}", token_str)))
    }
}

fn main() {
    let stdin = std::io::stdin();
    let mut reader = StdinReader::new(stdin.lock());

    loop {
        let target = reader.read::<i32>() / 10;
        if target == 0 { break; }
        let ns: Vec<i32> = (0..4).map(|_| reader.read()).collect();
        let ys = vec![1, 5, 10, 50];

        let total = ns.iter().zip(ys.iter())
            .fold(0, |sum, (n, y)| sum + n * y);
        let mut min_count = i32::max_value();
        let mut answer = Vec::new();
        for pay in target..(total + 1) {
            // 
            let mut rest = pay;
            let mut candidate = vec![0; 4];
            for i in (0..4).rev() {
                candidate[i] = min(rest / ys[i], ns[i]);
                rest -= candidate[i] * ys[i];
            }
            if rest != 0 { continue; }

            // 
            let mut otsuri = pay - target;
            let mut count = 0;
            for i in (0..4).rev() {
                count += otsuri / ys[i];
                otsuri -= otsuri / ys[i] * ys[i];
            }

            // 
            for i in 0..4 {
                count += ns[i] - candidate[i];
            }

            if count < min_count {
                answer = candidate;
                min_count = count;
            }
        }

        for i in 0..4 {
            if answer[i] > 0 {
                println!("{} {}", ys[i] * 10, answer[i]);
            }
        }
        println!();
    }
}

