						if(sum<n)continue;
						int tmp=s+change(sum-n);
						for(int i=0;i<4;i++)tmp-=a[i];
						if(tmp<mi){
							mi=tmp;
							for(int i=0;i<4;i++)ans[i]=a[i];
						}
