	  dif %= 500;
	  if ( dif ) t100 += dif/100;
	  dif %= 100;
	  if ( dif ) t50 += dif/50;
	  dif %= 50;
	  if ( dif ) t10 += dif/10;	  
	  tmp = t10+t50+t100+t500;
	  if ( tmp < sum ) {
	    min10  = t10;
	    min50  = t50;
	    min100 = t100;
	    min500 = t500;
	    a10  = i;
	    a50  = j;
	    a100 = k;
	    a500 = l;
	    sum = tmp;
