int main(){

	bool first = true;
	int pay;
	int c[4];
	int used[4] = {0};
	const int p[4] = {10, 50, 100, 500};

	while(cin >> pay, pay){

		if(!first){
			cout << endl;
		}

		first = false;

		for(int i=0; i<4; ++i){
			cin >> c[i];
		}

		CLR(used);

		for(int i=0; i<4; ++i){

			while(c[i] && pay > 0){
				pay -= p[i];
				c[i]--;
				used[i]++;
			}
		}

		if(pay < 0){

			pay *= -1;

			while(pay > 0){

				for(int i=3; i>=0; --i){

					while(pay >= p[i]){

						if(used[i] > 0){
							used[i]--;
						}

						pay -= p[i];
					}
				}

			}
		}

		for(int i=0; i<4; ++i){

			if(used[i] > 0){
				cout << p[i] << " " << used[i] << endl;
			}
		}

	}

	return 0;
}

