using namespace std;

int reverse_coin(int c){
    int cnt=0;
    while(1){
        if(c == 0){
            break;
        }
        if(500 <= c){  // c  =600
            c-=500;
            cnt++;
        }else if(100 <= c){
            c-=100;
            cnt++;
        }else if(50 <= c){
            c-=50;
            cnt++;
        }else if(10 <= c){
            c-=10;
            cnt++;
        }
    }
    return cnt;
}
int main(){
    int n,ju,goju,hya,gohya;
    int money[4],t=0,max=10000;
    int a=0,b=0,c=0,d=0;
    while(cin >> n,n){
        cin >> a >> b >> c >> d;
        for(int i=0;i<=a;i++){
            for(int j=0;j<=b;j++){
                for(int k=0;k<=c;k++){
                    for(int l=0;l<=d;l++){
                        if(n <= i*10+j*50+k*100+l*500){
                            int c = i*10+j*50+k*100+l*500 - n;
                            if(max > reverse_coin(c)){
                                max = reverse_coin(c);
                                ju = i;
                                goju = j;
                                hya = k;
                                gohya = l;
                            }
                        }
                    }
                }
            }
        }
        if(ju != 0){
            cout << 10 << ' ' << ju << endl;
        }

        if(goju != 0){
            cout << 50 << ' ' << goju << endl;
        }

        if(hya != 0){
            cout << 100 << ' ' << hya << endl;
        }

        if(gohya != 0){
            cout << 500 << ' ' << gohya << endl;
        }

    }
    return 0;
}

