using vlai=valarray<int>;

struct Wal{
    vlai c;
    Wal(){c=vlai(0,4);}
    Wal(vlai a){c=a;}
    void print(){
        if(c[0])cout<<"10 "<<c[0]<<endl;
        if(c[1])cout<<"50 "<<c[1]<<endl;
        if(c[2])cout<<"100 "<<c[2]<<endl;
        if(c[3])cout<<"500 "<<c[3]<<endl;
    }
};

int main(void){
    int n,h=0;
    vector<vlai> oturi(1,{0,0,0,0});
    rep(i,66*20){
        auto nxt=oturi.back();
        nxt[0]++;
        if(nxt[0]==5){
            nxt[0]=0,nxt[1]++;
            if(nxt[1]==2){
                nxt[1]=0,nxt[2]++;
                if(nxt[2]==5){
                    nxt[2]=0,nxt[3]++;
                }
            }
        }
        oturi.push_back(nxt);
    }
    while(cin>>n,n){
        n/=10;
        Wal p;
        vlai r={1,5,10,50};
        cin>>p.c[0]>>p.c[1]>>p.c[2]>>p.c[3];
        vector<Wal> val;
        rep(i,p.c[3]+1){
            rep(j,p.c[2]+1){
                rep(k,p.c[1]+1){
                    int total=50*i+10*j+5*k;
                    int d=n-total;
                    if(d>p.c[0])continue;
                    for(int l=0;l<p.c[0]+1;l++){
                        Wal q({l,k,j,i});
                        if(total+l-n>=0 && (oturi[total+l-n]*q.c).sum()==0)
                            val.push_back(q);
                    }
                }
            }
        }
        sort(all(val),[p,r,oturi,n](Wal a, Wal b){ return (p.c-a.c+oturi[(a.c*r).sum()-n]).sum()<(p.c-b.c+oturi[(b.c*r).sum()-n]).sum();});
        if(h++)cout<<endl;
        val.front().print();
    }
}

