		cin>>n;
		if(f)cout<<endl;//データセットを処理する2番目以降のみ改行
		if(n==0)break;
		int ans[4]={0};
		int table[4]={500,100,50,10};//高い順のテーブル
		int have[4];
		for(int i=0;i<4;i++)cin>>have[i];
		int goke=have[0]+have[1]+have[2]+have[3];

		for(int i=0;i<=have[0];i++){
			for(int j=0;j<=have[1];j++){
				for(int k=0;k<=have[2];k++){
					for(int l=0;l<=have[3];l++){
						int now=goke-(ans[0]+ans[1]+ans[2]+ans[3])+turi(ans[0]*10+ans[1]*50+ans[2]*100+ans[3]*500-n,ans[0],ans[1],ans[2],ans[3]);
						int nextsum=i*10+j*50+k*100+l*500;
