            for(int k = 0; k <= x3; k++){
                for(int l = 0; l <= x4;l++){
                    if(10*i+50*j+100*k+500*l < B) continue;
                    int X = x1 - i + x2 - j + x3 - k + x4 - l;
                    int leftB = 10*i+50*j+100*k+500*l - B;
                    X += leftB/500;
                    leftB %= 500;
                    X += leftB/100;
                    leftB %= 100;
                    X += leftB/50;
                    leftB %= 50;
                    X += leftB/10;
                    if(X < minX){
                        ret = vector<int>{i,j,k,l};
                        minX = X;
                    }
                }
            }
        }
    }
    cout << ret << endl;
    cout << minX << endl;
    int kane[4] = {10,50,100,500};
    for(int i = 0; i < 4;i++){
        if(ret[i] != 0){
            cout << kane[i] << " " << ret[i] << endl;
        }
    }
    cout << endl;
}
