	return 0;
}

int otsuri()
{
	int tsuri = shiharai - daikin;
	int cnt = 0;
	int mai = 0;
	for(int i = 3; i >= 0; i--){
		mai = tsuri / money[i];
		tsuri -= money[i] * mai;
		cnt += mai;
	}
	return cnt;
}

