int coins[4] = { 10,50,100,500 };

int  getans(const int money, const vector<int>&nums) {
	int turi = -money;
	for (int i = 0; i < 4; ++i) {
		turi += coins[i] * nums[i];
	}
	if (turi < 0)return 99999999;

	vector<int>turicoins(4);
	for (int i = 3; i >= 0; --i) {
		turicoins[i] = turi / coins[i];
		turi -= turicoins[i] * coins[i];
	}
	for (int i = 0; i < 4; ++i) {
		if (turicoins[i] && nums[i])return 99999999;
	}

	return accumulate(turicoins.begin(), turicoins.end(), 0);
}

