int main(){
    bool syokai=true;
    while(1){
        int N=0;
        scanf("%d",&N);
        if(N==0)return 0;
        if(syokai)syokai=false;
        else printf("\n");
        int n[5];// 10 円玉、50 円玉、100 円玉、500 円玉の枚数
        int n_kind[5]={10,50,100,500};
        int ans[5];
        int syojikin=0;
        rep(i,4){
            scanf("%d",&n[i]);
            syojikin+=n[i]*n_kind[i];
            ans[i]=0;
        }
        //全部払ってみる
        int over=syojikin-N;
        //店員がお釣りを返すことを考える
        //この時、500円から返す
        for(int i=3;i>-1;i--){
            ans[i]=max(n[i]-(over/n_kind[i]),0);
            over%=n_kind[i];
        }
        rep(i,4){
            if(ans[i]>0){
                printf("%d %d\n",n_kind[i],ans[i]);
            }
        }
    }
}

