#define REP(i, a, n) for(int i=a; i<n; i++)
#define RREP(i, a, n) for(int i=n-1; i>=a; i--)

int main() {
	int money;
	int value[4] = { 10, 50, 100, 500 };
	int ret[4];

	while (cin >> money, money) {
		int sum = 0;
		int coin[4];
		REP(i, 0, 4) {
			cin >> coin[i];
			sum += value[i] * coin[i];
			ret[i] = 0;
		}

		sum -= money;
		RREP(i, 0, 4) {
			while (sum >= value[i]) {
				ret[i]++;
				sum -= value[i];
			}
		}

		REP(i, 0, 4) {
			int pay = coin[i] - ret[i];
			if (pay > 0) cout << value[i] << " " << pay << endl;
		}
		cout << endl;
	}
	return 0;
}

