int coin[4];
int mn_count, mn_state;

int calc(int v) {
  int ret = 0;
  for (int i = 3; i >= 0; --i) {
    if (pows[i] <= v) {
      ret += v / pows[i];
      v %= pows[i];
    }
  }
  return ret;
}

void f(int cur, int state, int value) {
  if (cur == 4) {
    if (value < Y) return;
    int remain_count = calc(value - Y);
    // printf("%d %d :: %d %d ",cur, value, value, remain_count);
    if (mn_count > remain_count) {
      // printf("update");
      mn_state = state, mn_count = remain_count;
    }
    // puts("");
    return;
  }

  for (int i = 0; i <= coin[cur]; ++i) {
    f(cur+1, state | i << (cur * (5)), value + i * pows[cur]);
  }
}

