#include<iostream>
using namespace std;

const int INF = 1 << 30;

void solve(int N) {
    int cnt[4];
    for (int i = 0; i < 4; i++) {
        cin >> cnt[i];
    }
    int cnt_sum = INF;
    int ans[4] = {INF, INF, INF, INF};
    for (int i = 0; i <= cnt[0]; i++) {
        for (int j = 0; j <= cnt[1]; j++) {
            for (int k = 0; k <= cnt[2]; k++) {
                for (int l = 0; l <= cnt[3]; l++) {
                    int now_sum = 10 * i + 50 * j + 100 * k + 500 * l;
                    if (now_sum >= N) {
                        int now_rest = now_sum - N;
                        int now_cnt = 0;
                        now_cnt += cnt[0] - i;
                        now_cnt += cnt[1] - j;
                        now_cnt += cnt[2] - k;
                        now_cnt += cnt[3] - l;
                        if (now_rest / 500 && l) continue;
                        now_cnt += now_rest / 500;
                        now_rest %= 500;
                        if (now_rest / 100 && k) continue;
                        now_cnt += now_rest / 100;
                        now_rest %= 100;
                        if (now_rest / 50 && j) continue;
                        now_cnt += now_rest / 50;
                        now_rest %= 50;
                        if (now_rest / 10 && i) continue;
                        now_cnt += now_rest / 10;
                        if (now_cnt <= cnt_sum) {
                            cnt_sum = now_cnt;
                            ans[0] = i;
                            ans[1] = j;
                            ans[2] = k;
                            ans[3] = l;
                        }
                    }
                }
            }
        }
    }
    if (ans[0] > 0) cout << 10 << ' ' << ans[0] << endl;
    if (ans[1] > 0) cout << 50 << ' ' << ans[1] << endl;
    if (ans[2] > 0) cout << 100 << ' ' << ans[2] << endl;
    if (ans[3] > 0) cout << 500 << ' ' << ans[3] << endl;
}

int main() {
    cin.tie(0);
    ios::sync_with_stdio(false);
    int N;
    bool outputed = false;
    while (true) {
        cin >> N;
        if (N == 0) break;
        if (outputed) cout << '\n';
        solve(N);
        outputed = true;
    }
    return 0;
}

