using int64 = int64_t;

constexpr int64 MOD = 1000000007;

int price;
int coins[4];
int amounts[4] = {10, 50, 100, 500};

int calc_coins(int x) {
    int ret = 0;
    for (int j = 3; j >= 0; --j) {
        ret += x / amounts[j];
        x %= amounts[j];
    }
    return ret;
}

void solve() {
    int ans[4];
    int min_coins = 1 << 29;
    int init_coins = coins[0] + coins[1] + coins[2] + coins[3];
    for (int j = 0; j <= 20; ++j) if (j <= coins[0]) {
        for (int k = 0; k <= 20; ++k) if (k <= coins[1]) {
            for (int l = 0; l <= 20; ++l) if (l <= coins[2]) {
                for (int m = 0; m <= 20; ++m) if (m <= coins[3]) {
                    int pay_coins = j + k + l + m;
                    int pay = j * amounts[0] + k * amounts[1] + l * amounts[2] + m * amounts[3];
                    if (pay >= price) {
                        int change = pay - price;
                        int change_coins = calc_coins(change);

                        if (init_coins - pay_coins + change_coins < min_coins) {
                            min_coins = init_coins - pay_coins + change_coins;
                            ans[0] = j; ans[1] = k; ans[2] = l; ans[3] = m;
                        }
                    }
                }
            }
        }
    }

    for (int j = 0; j < 4; ++j) {
        if (ans[j] > 0) {
            cout << amounts[j] << ' ' << ans[j] << endl;
        }
    }
}

int main() {
    while (true) {
        cin >> price;
        if (price == 0) return 0;
        for (int j = 0; j < 4; ++j) cin >> coins[j];
        solve();
    }
    return 0;
}

