		int tmp = n %50;
		if(tmp/10 <= c[0])
		{
			n -= tmp;
			ans[0] += tmp/10;
			c[0] -= tmp/10;
			BR();

    		while(c[0] >= 5)
	    	{
				n -= 50;
				ans[0] += 5;c[0] -= 5;
				BR();
			}
		}
		if(n % 50)n += 5*(5 - n % 50);
		tmp = n % 100;
		if(tmp/50 <= c[1])
		{
			n -= tmp;
			ans[1] += tmp/50;
			c[1] -= tmp/50;
			BR();

			while(c[1] >= 2)
			{
				n -= 100;
				ans[2]+=2;c[1]-=2;
				BR();
			}
		}
		if(n % 100)n += 50;
		tmp = n % 500;
		if(tmp/100 <= c[2])
		{
			n -= tmp;
			ans[2] += tmp/100;
			c[2] = tmp/100;
			BR();
			while(c[2] >= 5)
			{
				n -= 500;
				ans[2]+=5;c[2]-=5;
				BR();
			}
		}
		BR();
		ans[3] += (n / 500);
		if(n % 500) ans[3]++;

		OUT:
		;
		REP(i,4)
		{
			if(ans[i])
			{
				cout << a[i] << ' ' << ans[i] << endl;
			}
		}

	}
	return 0;
}

