        cin >> a >> b >> c >> d;
        e=a+b+c+d;
        for(int i=0;i<=a;i++){
            for(int j=0;j<=b;j++){
                for(int k=0;k<=c;k++){
                    for(int l=0;l<=d;l++){
                        if(n <= i*10+j*50+k*100+l*500){
                            int c = i*10+j*50+k*100+l*500 - n;
                            if(max > e+reverse_coin(c)-i-j-k-l){
                                max = e+reverse_coin(c)-i-j-k-l;
                                ju = i;
                                goju = j;
                                hya = k;
