#include <iostream>

void coin(int n, int result[4]) {
    result[0] = (n % 50) / 10;
    result[1] = (n % 100) / 50;
    result[2] = (n % 500) / 100;
    result[3] = n / 500;
}

int main(void) {
    while(true) {
        int n = 0;
        std::cin >> n;
        if(n == 0){
            break;
        }

        int c10, c50, c100, c500;
        std::cin >> c10 >> c50 >> c100 >> c500;

        int result[4];
        coin(c10 * 10 + c50 * 50 + c100 * 100 + c500 * 500 - n, result);
        if(c10 - result[0] > 0)
            std::cout << "10 " << c10 - result[0] << std::endl;
        if(c50 - result[1] > 0)
            std::cout << "50 " << c50 - result[1] << std::endl;
        if(c100 - result[2] > 0)
