int v[4] = {10, 50, 100, 500};

int count_coin(int a, int b, int c, int d) {
    int sum = v[0]*a + v[1]*b + v[2]*c + v[3]*d - p;
    if (sum < 0) return INF;
    int change = 0;
    for (int i = 3; i >= 0; i--) {
        int t = sum /v[i];
        sum -= t * v[i];
        change += t;
        if (sum == 0) break;
    }
    return change + a + b + c + d;
}

int main() {
