            for (int j = 0; j <= n[1]; j++) 
                for (int k = 0; k <= n[2]; k++) 
                    for (int l = 0; l <= n[3]; l++) {
                        int t = count_coin(i, j, k, l);
                        if (min_coin > t) {
                            min_coin = t;
                            a = i, b = j, c = k, d = l;
