void output(int num_10, int num_50, int num_100, int num_500) {
	if (num_10 != 0) {
		cout << "10 " << num_10 << endl;
	}
	if (num_50 != 0) {
		cout << "50 " << num_50 << endl;
	}
	if (num_100 != 0) {
		cout << "100 " << num_100 << endl;
	}
	if (num_500 != 0) {
		cout << "500 " << num_500 << endl;
	}
