  ios_base::sync_with_stdio(false);

  bool flag = false;
  while(true){
	cin >> C; if(!C) break;
	REP(i,4) cin >> num[i];
	if(flag) cout << endl;

	int sum = 0;
	REP(i,4) sum += num[i] * M[i];
	C = sum - C;
	for(int i=3;i>=0;--i)
	  while(C >= M[i]){
		C -= M[i];
		--num[i];
	  }
	REP(i,4)
	  if(num[i] > 0) cout << M[i] << " " << num[i] << endl;
	flag = true;
  }

  return 0;
}

