int returnPay(int cost,int accept,int *state){
	if(cost>accept)return 10000000;

	int num = accept - cost;
//	cout<<"num= "<<num<<endl;

	int cnt=0;

	int data[4] ={500,100,50,10};
	int tmp[4];
	for(int i=0;i<4;i++){
		tmp[i]=0;
		while(num>=data[i]){
			cnt++;
			num-=data[i];
			tmp[i]++;
		}
	}

	int flag=0;
	int searchNum=-1;
	for(int i=0;i<4;i++){
		if(state[4-i-1]>0&&tmp[i]>0){
			return 10000000;
		}
	}


	//cout<<"num="<<accept-cost<<endl;
	return cnt;
}

void makePattern(int *state,int *data,int pos,int n,int cost){

	int okane[4]={500,100,50,10};

	if(pos==n){
		int sum=0;
		int maisuu=0;
		for(int i=0;i<n;i++){
			sum+=state[n-1-i]*okane[i];
			maisuu+=(data[n-1-i]-state[n-1-i]);
	//		cout<<state[i]<<" ";
		}
	//	cout<<endl;
	//	cout<<"sum= "<<sum<<endl;

		maisuu+=returnPay(cost,sum,state);
	//	cout<<"tmp= "<<tmp<<endl;
		if(maisuu<=min2){
			int flag=0;

			if(flag==0){
				min2=maisuu;
				for(int i=0;i<n;i++){
					ans[i]=state[i];
				}
			}
		}
	}


	else{
	for(int i=0;i<=data[pos];i++){
		state[pos]=i;
		makePattern(state,data,pos+1,n,cost);
	}
	}
}



