int chng(int k, int bi){
    int cnt = 0; 
    while(k / 500){
        if(bi & (1<<3)) {
            //cout << " a " << k << " " << bi << endl;
            return INF;
        }
        k /= 500;
        cnt++;
    }
    while(k / 100){
        if(bi & (1<<2)) return INF;
        k /= 100;
        cnt++;
    }
    while(k / 50){
        if(bi & (1<<1)) return INF;
        k /= 50;
        cnt++;
    }
    while(k / 10){
        if(bi & (1<<0)) return INF;
        k /= 10;
        cnt++;
    }

    return cnt;
}

