vector<int> cal(int val, vector<int> purse) {
	vector<int> res(4, 0);
	int tmp;

	tmp = (val % 50) / 10;
	if(tmp <= purse[0]) {
		res[0] += tmp;
		purse[0] -= tmp;
	}
	tmp = purse[0] / 5;
	res[0] += tmp * 5;
	val -= res[0] * 10;

	tmp = val / 50;
	if(tmp <= purse[1]) {
		res[1] += tmp;
		purse[1] -= tmp;
	}else{
		if(tmp % 2 == 1) {
			if(purse[1] % 2 == 1) {
				res[1] += purse[1];
			}else{
				res[1] += purse[1] - 1;
			}
		}else{
			if(purse[1] % 2 == 1) {
				res[1] += purse[1] - 1;
			}else{
				res[1] += purse[1];
			}
		}
	}
	val -= res[1] * 50;

	tmp = (val % 500) / 100;
	if(tmp <= purse[2]) {
		res[2] += tmp;
		purse[2] -= tmp;
	}
	tmp = purse[2] / 50;
	res[2] += tmp * 50;
	val -= res[2] * 100;

	if(val != 0) {
		tmp = val / 500;
		res[3] = tmp + 1;
	}

	return res;
}

int main(void) {
	int val;
