	while(1) {
		cin >> val;
		if(val == 0) {
			break;
		}

		if(frag) {
			cout << endl;
		}
		frag = true;

		vector<int> purse(4);
		for(int i = 0; i < 4; i++) {
			cin >> purse[i];
		}

		vector<int> res = cal(val, purse);
		out(res);
	}
	return 0;
}

