		for (int i = 0; i < 4; i++)
		{
			cin >> kind[i];
			kindSum += kind[i];
		}

		int min = 100000;
		int ans = 0;
		for (int i = 0; i <= kind[0]; i++)
		{
			int ii = i * 10;
