using namespace std;

int main()
{

	while(1){
		int N, a, b, c, d;
		int N2, a2=0, b2=0, c2=0, d2=0;

		cin >> N;
		if(N==0){return 0;}
		cin >> a >> b >> c >> d;
		N2=N;
 		for(int i=0; i<(double)N2/10; i++){
			if(a==0 || N<=0 || (a<5 && N%50==0)){break;}
			a--; a2++; N = N-10; 
		}
		N2=N;
 		for(int i=0; i<(double)N2/50; i++){
			if(b==0 || N<=0){break;}
			b--; b2++; N = N-50;
		}
		N2=N;
 		for(int i=0; i<(double)N2/100; i++){
			if(c==0 || N<=0 || (c<5 && N%500==0)){break;}
			c--; c2++; N = N-100;
		}
		N2=N;
 		for(int i=0; i<(double)N2/500; i++){
			if(d==0 || N<=0){break;}
			d--; d2++; N = N-500;
		}
		if(a2 != 0){cout << "10 " << a2 << endl; }
		if(b2 != 0){cout << "50 " << b2 << endl; }
		if(c2 != 0){cout << "100 " << c2 << endl; }
		if(d2 != 0){cout << "500 " << d2 << endl; }
		cout << endl;
	}
}

