#include <iostream>
#include <vector>
using namespace std;

int main()
{
  int c[4] = {10, 50, 100, 500};
  vector<int> num; //所持している硬貨の枚数
  int pay; //支払金額

  while(1) {
    cin >> pay;
    if(pay == 0)
      break;

    for(int i = 0; i < 4; i++) {
      int C;
      cin >> C;
      num.push_back(C);
    }

    int sum = 0;
    for(int i = 0; i < 4; i++) {
      sum += c[i] * num[i];
    }
    pay = sum - pay;

    int k[4] = {}; //余った硬貨の枚数

    for(int i = 3; i >= 0; i--){
      k[i] = pay / c[i];
      pay = pay - (k[i] * c[i]);
    }

    for (int i = 0; i < 4; i++) {
      if(num[i] > k[i]) {
        cout << c[i] << ' ' << num[i] - k[i] << endl;
      }
    }

    cout << endl;
    num.clear();
  }
}

