int money[] = {10, 50, 100, 500};

vector<int> coin(int m) {
    vector<int> ret(4);
    repr(i,3,0) {
        if(m < money[i]) continue;
        ret[i] = m / money[i];
        m -= (m / money[i] * money[i]);
    }
    return ret;
}

signed main() {
    int n;
    while(cin >> n, n) {
        vector<int> ans(4);
        int c[5] = {};
        int cntcoin = 0;
        rep(i,0,4) {
            cin >> c[i];
            cntcoin += c[i];
        }

        int mi = INF;
        repq(i,0,c[0]) repq(j,0,c[1]) repq(k,0,c[2]) repq(l,0,c[3]) {
            int usecoin = i+j+k+l;
            int temp = i*money[0] + j*money[1] + k*money[2] + l*money[3];
            if(temp < n) continue;

            vector<int> backcoin = coin(temp - n);
            if(backcoin[0] && i) continue;
            if(backcoin[1] && j) continue;
            if(backcoin[2] && k) continue;
            if(backcoin[3] && l) continue;
            int cntbackcoin = 0;
            rep(i,0,4) cntbackcoin += backcoin[i];
            if(mi > (cntcoin - usecoin + cntbackcoin)) {
                mi = cntcoin - usecoin + cntbackcoin;
                ans = vector<int>{i, j, k, l};
            }
        }

        rep(i,0,4) {
            if(ans[i]) printf("%lld %lld\n", money[i], ans[i]);
        }
    }
    return 0;
}

