int f(int a, int b, int c, int d, int n) {
  int p = (10*a + 50*b + 100*c + 500*d) - n;
  if (p < 0) return 100;

  int q, r, s, t;
  q = p / 500;
  p %= 500;
  r = p / 100;
  p %= 100;
  s = p / 50;
  p %= 50;
  t = p / 10;

  if ((a && t) || (b && s) || (c && r) || (d && q)) return 100;
  return q+r+s+t;
}

