using namespace std;

struct pay{
	int c[4], sum, num;
	bool operator<( const pay& right ) const {
        return sum == right.sum ? c[0] < right.c[0] : sum < right.sum;
    }
};

int main(void){
	//FILE* fp_in = freopen("data.txt", "r", stdin);

	int cnt = 0;
	while(1) {
		vector<pay> p;
		int P, c[4];
		int coin[] = {10, 50, 100, 500};
		cin >> P;
		if( P == 0 ) break;

		if( cnt > 0 ) cout << endl;

		cin >> c[0] >> c[1] >> c[2] >> c[3];

		//支払い可能なパターンを全探索
		for(int i = 0; i <= c[0]; i++) {
			for(int j = 0; j <= c[1]; j++) {
				for(int k = 0; k<= c[2]; k++) {
					for(int l = 0; l <= c[3]; l++) {
						if( P <= i * 10 + j * 50 + k * 100 + l * 500 ) {
							pay np;
							np.c[0] = i;
							np.c[1] = j;
							np.c[2] = k;
							np.c[3] = l;
							np.num = i + j + k + l;
							np.sum = i * 10 + j * 50 + k * 100 + l * 500;
							p.push_back( np );
						}
					}
				}
			}
		}

		//つり銭の数
		vector<int> chg;
		for(int i = 0; i < p.size(); i++) {
			int num, tmp = 0;
			num = p.at(i).sum - P;

			for(int j = 3; j >= 0; j--) {
				tmp += num / coin[j];
				num %= coin[j];
			}

			chg.push_back(tmp);
		}

		//硬貨合計
		vector<int> coins;
		for(int i = 0; i < p.size(); i++) {
			int tmp = c[0] + c[1] + c[2] + c[3] - p.at(i).num + chg.at(i);
			coins.push_back( tmp );
		}

		int indx = 0, min = 1 << 10;
		for(int i = 0; i < p.size(); i++) {
			if( coins.at(i) < min ) {
				min = coins.at(i);
				indx = i;
			}
		}

		for(int i = 0; i < 4; i++) {
			if(  p.at(indx).c[i] == 0 ) continue;
			cout << coin[i] << " " << p.at(indx).c[i] << endl;
		}
		cnt ++;
	}

	//while(1){}

	return 0;
}

