        coin[0] = make_pair(10, 0);

        /* 持っているお金の合計値を算出し可能な限りの両替を行う */
        int sum = 10*num[0] + 50*num[1] + 100*num[2] + 500*num[3];
        cout << sum << endl;
        for (int i = 3; i >= 0; i--) {
            coin[i].second = sum / coin[i].first;
            sum -= coin[i].first * coin[i].second;
        }

        vector<int> div(4);
        for (int i = 3; i >= 0; i--) {

            /* コインを支払うべき枚数を決定する */
            div[i] = n / coin[i].first;

            /* 持っているコインがなければ一つ上のコインを両替する */
            if (div[i] > coin[i].second) {
                coin[i+1].second -= 1;
                coin[i].second += (coin[i+1].first / coin[i].first - div[i]);
            }

            /* 支払い後は合計値から支払った分だけ引く */
            else {
                coin[i].second -= div[i];
            }

            n -= div[i] * coin[i].first;
        }

        /* 支払いをしたのか，おつりが来たのかを判断する */
        for (int i = 0; i < 4; i++) {
            if (coin[i].second < num[i])
            cout << coin[i].first << " " << coin[i].second << endl;
        }

